    changed = False
    for acc in accounts:
        if "name" not in acc or not acc["name"]:
            # –ü–æ–ø—Ä–æ–±—É–µ–º –≤–∑—è—Ç—å –∏–º—è –∏–∑ username –∏–ª–∏ phone
            acc["name"] = acc.get("username") or acc.get("phone")
            changed = True
    return changed



